<?php

namespace AGORA\Game\SQPBundle\Repository;

/**
 * SQPLeaderBoardRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SQPLeaderBoardRepository extends \Doctrine\ORM\EntityRepository
{
    public function getLeaderBoardJoin() {
        return $this->createQueryBuilder('a')
            ->join('a.idPlayer', 'u')
            ->addOrderBy('a.eLO', 'DESC')
            ->getQuery()->getResult();

    }
}
